pragma solidity >=0.4.22 <0.9.0;

contract CrowdFunding
{
    string public name = "Crowd Funding smart contract";

    //working with ether only here

    event EtherSent(
    address account,
    uint amount

  );

    //transfers ether from sender to smart contract / liquidity pool
    function sendcoins(address payable sender) public payable 
    {
        uint EtherAmount = msg.value;

        require(address(msg.sender).balance >= EtherAmount);

        sender.transfer(EtherAmount);

        emit EtherSent(sender, EtherAmount);


    }




}
